
from leetcode.longest_palindromic_substring.solution import Solution
from core.aesthetics import *

class TestCase:
    def __init__(self, testcase, correct_result, function):
        self.testcase = testcase
        self.correct_result = correct_result
        
        if not callable(function):
            raise InterruptedError("function object is not a function")
        self.function = function
        
    def compute(self):
        self.function_result = self.function(self.testcase)
        return self.correct_result == self.function_result
    
testcases = [
    ("babad", "bab"),
    ("cbbd", "bb"),
    ("a", "a"),
    ("c", "c"),
    ("ac", "a"),
    ("asjidbgasivyhd123", "a"),
    ("asujiodgayisfgv12222", "2222"),
    ("", ""),
    ("", ""),
    ("", "")
]

def TestAlgorithm(testcases: list):
    for __index, __testcase in enumerate(testcases, start=1):
        if __testcase.compute():
            result = colored_green_bold("passed")
        else:
            result = colored_red_bold("failed")
        message = f"testcase #{__index}: '{__testcase.testcase}' -> '{__testcase.function_result}' ... {result}."
        print(message)

if __name__ == '__main__':
    # importing solution
    solution_class = Solution()
    
    # creating testcases
    testcases = [
        TestCase(testcase[0], testcase[1], solution_class.longestPalindrome) for testcase in testcases
    ]
    
    # testing testcases
    TestAlgorithm(testcases)
    